import "./FrontPageAnimation.css"
import React from "react";
const FrontPageAnimation = () => {
    const target = document;

    
    function factorfinder() {
        var w = window.innerWidth;
        var factor;
        if (w <= 500) {
            factor = 0.15;
        }
        else if (w <= 780 && w > 500) {
            factor = 0.2;
        }
        else {
            factor = 0.15;
        }
        console.log(factor);
        return factor;
    }
    console.log("hi");

    target.addEventListener("scroll", function () {
        var h = window.screen.height;
        var factor = factorfinder();
        if (window.pageYOffset < factor * h) {
            document.getElementById("hero-scene").classList.add("deanimate");
            document.getElementById("hero-scene").classList.remove("animate");
        }
        else if (window.pageYOffset > factor * h) {
            document.getElementById("hero-scene").classList.add("animate");
            document.getElementById("hero-scene").classList.remove("deanimate");
        }
    })



    return (
        <div class="animationhai">
            <section class="hero" id="hero-scene">
                <section class="move pd-0">
                    <div class="container" id="car">
                        <section class="treatment pd-0">
                            <img
                                src="https://kusama.network/assets/img/parachains/KUSAMA-GIFS_3.gif"
                                alt="new"
                                id="GIF"
                            />
                            <div class="text">
                                <h2>My name is hatori</h2>
                            </div>
                        </section>

                    </div>
                </section>
            </section>
        </div>
    )
};

export default FrontPageAnimation;
